import React, { useState } from 'react';
import './add.css'; // Import your CSS file for styling

const AddRoomPage = () => {
  const [formData, setFormData] = useState({
    type: '',
    furnishing: '',
    carpet_area: '',
    total_floor: '',
    floor_number: '',
    owner_name: '',
    price: '',
    location: '',
    contact_number: '',
    email: '',
    image1: null,
    image2: null,
    image3: null
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleImageChange = (e, index) => {
    const file = e.target.files[0];
    setFormData({
      ...formData,
      [`image${index}`]: file
    });
    // Optionally, you can preview the selected image here
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to server
    console.log(formData);
  };

  return (
    <div className="container">
      <h2 className='addH'>Add Room for Rent</h2>
      <form onSubmit={handleSubmit}>
        {/* Other form fields */}
        
        {/* Image upload fields */}
        <div className="image-box">
          <label htmlFor="image1">Add Image 1:</label>
          <input type="file" id="image1" accept="image/*" onChange={(e) => handleImageChange(e, 1)} />
        </div>
        <div className="image-box">
          <label htmlFor="image2">Add Image 2:</label>
          <input type="file" id="image2" accept="image/*" onChange={(e) => handleImageChange(e, 2)} />
        </div>
        <div className="image-box">
          <label htmlFor="image3">Add Image 3:</label>
          <input type="file" id="image3" accept="image/*" onChange={(e) => handleImageChange(e, 3)} />
        </div>
        {/* Image previews */}
        <div className="image-preview">
          {formData.image1 && <img src={URL.createObjectURL(formData.image1)} alt="Preview 1" />}
          {formData.image2 && <img src={URL.createObjectURL(formData.image2)} alt="Preview 2" />}
          {formData.image3 && <img src={URL.createObjectURL(formData.image3)} alt="Preview 3" />}
        </div>

        <button type="submit">Submit</button>
        <div id="error-message" className="error-message"></div>
      </form>
    </div>
  );
};

export default AddRoomPage;
